package aoc;

public class FindStarsP2Q2 {

    private final char[][] matrix = {
            {'*', '*', '5', '*', '*'},
            {'*', '2', '6', 'A', '*'},
            {'1', '3', '7', 'B', 'D'},
            {'*', '4', '8', 'C', '*'},
            {'*', '*', '9', '*', '*'}};

    private int curX = 0;
    private int curY = 2;

    private void parseInput(String s) {
        for (int i = 0; i < s.length(); i++) {
            switch (s.charAt(i)) {
                case 'D':
                    if (curY != 4 && matrix[curX][curY + 1] != '*') curY++;
                    break;
                case 'L':
                    if (curX != 0 && matrix[curX - 1][curY] != '*') curX--;
                    break;
                case 'R':
                    if (curX != 4 && matrix[curX + 1][curY] != '*') curX++;
                    break;
                case 'U':
                    if (curY != 0 && matrix[curX][curY - 1] != '*') curY--;
                    break;
            }
        }
        System.out.println(matrix[curX][curY]);
    }

    public static void main(String[] args) {
        FindStarsP2Q2 fs = new FindStarsP2Q2();

        String[] inputs = {"",
                "",
                "",
                "",
                ""};

        for (String input : inputs) {
            fs.parseInput(input);
        }

    }
}